import React from "react";
import Graph from "../shared/graph";
import Card from "../shared/card";
import Header from "../shared/header"
import ReportOverview from "./reportOverview";

import getData from "../firebase/getData";


import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
} from "react-native-chart-kit";


import {Text, View, StyleSheet, FlatList, ScrollView, SafeAreaView, TouchableOpacity} from 'react-native'

const data = [
    {
        incassi: 300,
        spese: 100, 
        utile: 222
    },
    {
        incassi: 344,
        spese: 14, 
        utile: 242
    },
    {
        incassi: 100,
        spese: 140, 
        utile: 252
    },
    {
        incassi: 344,
        spese: 14, 
        utile: 242
    },
    {
        incassi: 100,
        spese: 140, 
        utile: 252
    }
]



function onPressCard(navigation, item){
    navigation.navigate('ReportOverview')
    console.log('Ciao');
    getData()
        .then()
}


export default function Home({navigation}){

    return(
        <View>
            {/*<Header*/}

            <SafeAreaView style={styles.container}>
                <ScrollView>
                <View style={styles.resocontoView}>
                    <Text style={styles.bigBoldText}>Benvenuto</Text>
                    <Text style={styles.normalText}>Ecco i resconti del mese:</Text>

                </View>

                    {<FlatList
                        data={data}
                        renderItem={({item}) => (
                            <TouchableOpacity onPress={(item) => onPressCard(navigation, item)}>
                                <Card>
                                    <Graph data={item}></Graph>
                                </Card>
                            </TouchableOpacity>

                        )}
                        style={styles.flatlistView}
                    />}
                </ScrollView>
            </SafeAreaView>

        </View>
    );

}

const styles = StyleSheet.create({
    resocontoView: {
        alignSelf: 'flex-start',

    },
    flatlistView: {
        backgroundColor: 'blue',
        marginTop: 15
    },
    bigBoldText: {
        fontSize: 40,
        fontWeight: 'bold',
    },
    normalText: {
        fontSize: 20
    },
    container:{
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'pink'
    }
});
