import React from "react";
import Graph from "../shared/graph";
import Card from "../shared/card";
import Header from "../shared/header"

import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
} from "react-native-chart-kit";

import {Text, View, StyleSheet, FlatList} from 'react-native'

const data = [
    {
        incassi: 300,
        spese: 100, 
        utile: 222
    },
    {
        incassi: 344,
        spese: 14, 
        utile: 242
    },
    {
        incassi: 100,
        spese: 140, 
        utile: 252
    },
    {
        incassi: 344,
        spese: 14, 
        utile: 242
    },
    {
        incassi: 100,
        spese: 140, 
        utile: 252
    }
]

export default function Home(){
    return(
        <View>
            <Header/>


            <View style={styles.container}>
            <View style={styles.resocontoView}>
                <Text style={styles.bigBoldText}>Benvenuto</Text>
                <Text style={styles.normalText}>Ecco i resconti del mese:</Text>

            </View>

                {<FlatList
                    data={data}
                    renderItem={({item}) => (
                        <Card>
                            <Graph data={item}></Graph>
                        </Card>
                    )}
                />}
            </View>
        </View>
    );

}

const styles = StyleSheet.create({
    resocontoView: {
        alignSelf: 'flex-start',
        backgroundColor: 'blue',
        margin: 30,
        marginVertical: 30
    },
    bigBoldText: {
        fontSize: 40,
        fontWeight: 'bold',
    },
    normalText: {
        fontSize: 20
    },
    container:{
        justifyContent: 'center',
        alignItems: 'center'
    }
});
