import {Text, StyleSheet, View, FlatList, Button, TextInput} from 'react-native'
import {React, useState} from 'react'
import Modal from "react-native-modal";
import DatePicker from '../shared/datePicker';
import DataList from './dataList';

export default function ReportOverview({route, navigation}){
    const data = route.params
    const [isModalVisible, setModalVisible] = useState(false);
    const [isAddingIncome, setAddingIncome] = useState();

    const toggleModal = (incomeButton) => {
        if(incomeButton)
            setAddingIncome(true);
        else
            setAddingIncome(false);

        setModalVisible(!isModalVisible);
    };

    return(
        <View style={styles.container}>
            <Text style={styles.normalText}>{data.timeSpanName}</Text>
            <Text style={styles.normalBoldText}>Incomes:</Text>
            <Text style={styles.bigBoldText}>100,0</Text>

            <Button title="Add Expense" onPress={() => toggleModal(false)}/>
            <Button title="Add Income" onPress={() => toggleModal(true)}/>
            <ModalTester toggleModal={toggleModal} isModalVisible={isModalVisible} isAddingIncome={isAddingIncome}/>
            <DataList rawData={data.rawData}/>
        </View>

    );
}


function ModalTester(props) {
    const [date, setDate] = useState(new Date());
    const [showDatePicker, setShowDatePicker] = useState(false);

    const addIncomePart = 
        <View>
            <Text style={styles.mediumBoldText}>Add an income:</Text>
            <TextInput style={styles.normalText} placeholder='Insert Value'></TextInput>
        </View>


    const addExpensesPart = 
        <View>
            <Text style={styles.mediumBoldText}>Add an expense:</Text>
            <TextInput style={styles.normalText} placeholder='Insert value'></TextInput>
        </View>

    return (
      <View style={{flex:1, justifyContent: 'center', alignItems: 'center'}}>  
        <Modal style={styles.modal} isVisible={props.isModalVisible}>
          <View >
            <Text style={styles.mediumBoldText}>Date:</Text>

            <View style={styles.dateAndButtonView}>

                <View style={styles.date}>
                    <Text style={styles.normalText}>{date.toLocaleDateString()}</Text>            
                </View>

                <View  style={styles.changeButton}>
                    <Button title='CHANGE' onPress={() => setShowDatePicker(true)}/>
                </View>

            </View>

            <DatePicker date={date} setDate={setDate} show={showDatePicker} setShow={setShowDatePicker}/>

            <View style={styles.valueView}>

                { props.isAddingIncome ? addIncomePart : addExpensesPart}

            </View>
            {/* Sistemare altezza */}
            <Button style={styles.insertButton} title="Insert" onPress={props.toggleModal} />



          </View>
        </Modal>
      </View>
    );
  }

const styles = StyleSheet.create({

    bigBoldText: {
        fontSize: 40,
        fontWeight: 'bold',
    },
    mediumBoldText: {
        fontSize: 30,
        fontWeight: 'bold',
    },
    normalText: {
        fontSize: 20
    },
    container:{
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'pink'
    },
    modal:{
        padding: 30,
        flex: 0,
        backgroundColor: 'white',

    },
    dateAndButtonView:{
        flexDirection: "row",
        justifyContent: "space-between",
        /*backgroundColor: 'red'        */

    },
    changeButton: {
        flex: 1,
        marginLeft: 100,
    },
    date:{
        flex: 1,
        justifyContent: 'center',
        /*backgroundColor: 'blue'*/
    },
    valueView:{
        marginTop: 20,

    },
    insertButton:{
        marginTop: 40
    }


});
