import {React, useState} from "react";
import Graph from "../shared/graph";
import Card from "../shared/card";
import Header from "../shared/header"
import ReportOverview from "./reportOverview";

import getData from "../firebase/getData";


import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
} from "react-native-chart-kit";


import {Text, View, StyleSheet, FlatList, ScrollView, SafeAreaView, TouchableOpacity, Button} from 'react-native'
import getUserTriplets from "../firebase/getData";

const USERNAME = 'Zeta'

var data2 = [];




function onPressCard(navigation, item){
    navigation.navigate('ReportOverview')

}

async function  onPressQuery(){
    getUserTriplets(USERNAME)
    .then((returnData) => {
        console.log(returnData);
        return returnData;
    })


}

var i = 0;
export default function Home({navigation}){

    const [data, setData] = useState(
        [
            {
                Incassi: 300,
                Spese: 100, 
                Utile: 222
            },
            {
                Incassi: 344,
                Spese: 14, 
                Utile: 242
            }
        ]
    );

    i+=1;
    console.log(i+": "+data);

    return(
        <View>
            {/*<Header*/}

            <SafeAreaView style={styles.container}>
                <ScrollView>
                <View style={styles.resocontoView}>
                    <Text style={styles.bigBoldText}>Benvenuto</Text>
                    <Button title='Query' onPress={() => onPressQuery().then((returnData) => setData(returnData))}/>
                    <Text style={styles.normalText}>Ecco i resconti del mese:</Text>

                </View>

                    {<FlatList
                        data={data}
                        renderItem={({item}) => (
                            <TouchableOpacity onPress={(item) => onPressCard(navigation, item)}>
                                <Card>
                                    <Graph data={item}></Graph>
                                </Card>
                            </TouchableOpacity>

                        )}
                        style={styles.flatlistView}
                    />}
                </ScrollView>
            </SafeAreaView>

        </View>
    );

}

const styles = StyleSheet.create({
    resocontoView: {
        alignSelf: 'flex-start',

    },
    flatlistView: {
        backgroundColor: 'blue',
        marginTop: 15
    },
    bigBoldText: {
        fontSize: 40,
        fontWeight: 'bold',
    },
    normalText: {
        fontSize: 20
    },
    container:{
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'pink'
    }
});
