import {React, useState} from "react";
import Graph from "../shared/graph";
import Card from "../shared/card";
import Header from "../shared/header"
import ReportOverview from "./reportOverview";

import getData from "../firebase/getData";
import {tempData, tempSettings, filterMonthly} from "./homeLogic"

import {
    LineChart,
    BarChart,
    PieChart,
    ProgressChart,
    ContributionGraph,
    StackedBarChart
} from "react-native-chart-kit";


import {Text, View, StyleSheet, FlatList, ScrollView, SafeAreaView, TouchableOpacity, Button} from 'react-native'
import getUserTriplets from "../firebase/getData";



function onPressCard(navigation, item){
    navigation.navigate('ReportOverview', item)

}

function populatePage(timeSpanMode){
    if(timeSpanMode === "MONTHLY"){
        const tmp = filterMonthly(tempData);
        return tmp;
        //console.log(tmp[0].tripletTotal);

    }
}


var i = 0;
export default function Home({navigation}){

    const [data, setData] = useState(tempData);

    i+=1;

    return(
        <View>
            {/*<Header*/}

            <SafeAreaView style={styles.container}>
                <ScrollView>
                <View style={styles.resocontoView}>
                    <Text style={styles.bigBoldText}>2020</Text>
{/*
                    <Button title='Query' onPress={() => getUserTriplets(USERNAME).then((returnData) => {
                        setData(returnData);
                        console.log(i+": "+returnData);

                        })}/>
*/}

                    <Text style={styles.normalText}>Ecco i resconti del mese:</Text>

                </View>

                    {<FlatList
                        data={populatePage(tempSettings.TIMESPAN)}
                        renderItem={({item}) => (
                            <TouchableOpacity onPress={() => onPressCard(navigation, item)}>
                                <Card>
                                    <Graph data={item.tripletTotal}></Graph>
                                </Card>
                            </TouchableOpacity>

                        )}
                        style={styles.flatlistView}
                        />}
                </ScrollView>
            </SafeAreaView>

        </View>
    );

}

const styles = StyleSheet.create({
    resocontoView: {
        alignSelf: 'flex-start',

    },
    flatlistView: {
        backgroundColor: 'blue',
        marginTop: 15
    },
    bigBoldText: {
        fontSize: 40,
        fontWeight: 'bold',
    },
    normalText: {
        fontSize: 20
    },
    container:{
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'pink'
    }
});
